#Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?

def largestproductinseries(num,numdigits):
    num = str(num)
    maxproduct = 0
    for i in range(0,len(num)-numdigits):
        product = productcalc(num[i:i+numdigits])
        if product > maxproduct:
            maxproduct = product
    return maxproduct

def productcalc(numstr):
    product = 1
    for i in range(0,len(numstr)):
        product = product * int(numstr[i])
    return product
